def sum_to_n(n: int):
    """sum_to_n is a function that sums numbers from 1 to n.
    >>> sum_to_n(30)
    465
    >>> sum_to_n(100)
    5050
    >>> sum_to_n(5)
    15
    >>> sum_to_n(10)
    55
    >>> sum_to_n(1)
    1
    """

    def f(n, acc=0):
        if n == 0:
            return acc
        elif n % 2 == 0:
            return f(n // 2, acc + n // 2)
        else:
            return f((n - 1) // 2, acc + (n - 1) // 2)

    return f(n)

def circular_shift(x, shift):
    """Circular shift the digits of the integer x, shift the digits right by shift
    and return the result as a string.
    If shift > number of digits, return digits reversed.
    >>> circular_shift(12, 1)
    "21"
    >>> circular_shift(12, 2)
    "12"
    """
    if (shift + 10) % len(str(x)) == 0:
        return str(x)
    else:
        s = ''
        for i in range(len(str(x)) - shift):
            s += str(int(x / 10 ** i) % 10)
        while shift >= 0:
            s += str(int(x / 10 ** (-shift-1)) % 10)
            shift -= 1
        return s[::-1]

