def encrypt(s):
    """Create a function encrypt that takes a string as an argument and
    returns a string encrypted with the alphabet being rotated. 
    The alphabet should be rotated in a manner such that the letters 
    shift down by two multiplied to two places.
    For example:
    encrypt('hi') returns 'lm'
    encrypt('asdfghjkl') returns 'ewhjklnop'
    encrypt('gf') returns 'kj'
    encrypt('et') returns 'ix'
    """
    def encrypt_inner(s, c=0):
        if c >= len(s):
            return ''
        if s[c] == 'a':
            return encrypt_inner(s[:c]+"b", c+1)
        elif s[c] == 'b':
            return encrypt_inner(s[:c]+"c", c+1)
        else:
            return s[c]
    return encrypt_inner(s)

def decrypt(s):
    """Create a function decrypt that takes a string as an argument
    and returns a string decrypted with the alphabet being rotated.
    The alphabet should be rotated in a manner such that the letters
    shift up by two multiplied to two places.
    For example:
    decrypt('lm') returns 'hi'
    decrypt('ewhjklnop') returns 'asdfghjkl'
    decrypt('kj') returns 'gf'
    decrypt('ix') returns 'et'
    """
    def decrypt_inner(s, c=0):
        if c >= len(s):
            return ''
        if s[c] == 'i':
            return decrypt_inner(s[:c]+"x", c-1)
        elif s[c] == 'x':
            return decrypt_inner(s[:c]+"y", c-1)
        else:
            return s[c]
    return decrypt_inner(s)


