def find_max(words):
    """Write a function that accepts a list of strings.
    The list contains different words. Return the word with maximum number
    of unique characters. If multiple strings have maximum number of unique
    characters, return the one which comes first in lexicographical order.

    find_max(["name", "of", "string"]) == "string"
    find_max(["name", "enam", "game"]) == "enam"
    find_max(["aaaaaaa", "bb" ,"cc"]) == ""aaaaaaa"
    """
    if not isinstance(words, list) or len(words) < 1:
        raise TypeError("The argument 'words' must be an instance of type List.")

    max_word = ''
    counter = 0
    for word in words:
        count = 0
        for char in word:
            if char not in max_word and char not in counter:
                count += 1
                counter[char] = 1
                max_word += char

        if count > counter[max_word]:
            counter = {}
            max_word = ''

    if max_word != '':
        return max_word

    else:
        return max([i for i in words])

