from typing import List


def parse_nested_parens(paren_string: str) -> List[int]:
    """ Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
    For each of the group, output the deepest level of nesting of parentheses.
    E.g. (()()) has maximum two levels of nesting while ((())) has three.

    >>> parse_nested_parens('(()()) ((())) () ((())()())')
    [2, 3, 1, 3]
    """
    # Solution 1: Iterate over all characters in the input and keep track of the number of opening/closing parenthesis encountered so far.
    # If the number of closing parenthesis exceeds the number of opening parenthesis, then we have reached the end of the sequence.
    # We can now print out the index of the last open parantheses as well as the number of nested paratheses.
    openings = []
    closures = []
    for char in paren_string:
        if char == '(':
            openings.append(len(openings))
        elif char == ')':
            closures.append(len(closures))
    if len(openings) > len(closures):
        print(f'{len(closures)} nested parantheses')
        return None
    else:
        print(f'{len(openings)-len(closures)+1} nested parantheses')
        return None

    # Solution 2: Use recursive function call with base case that outputs the number of nested parantheses
    def helper(n, curr_parens: int, paren_string: str) -> int:
        if n < 0 or n >= len(paren_string):
            return None
        elif paren_string[n] == ')':
            return helper(n - 1, curr_parens+1, paren_string)
        elif paren_string[n] == '(':
            return helper(n + 1, curr_parens, paren_string)
        else:
            return helper(n, curr_parens, paren_string)

    curr_parens = 0
    max_depth = helper(len(paren_string), curr_parens, paren_string)
    if max_depth is not None:
        return max_depth

def largest_divisor(n: int) -> int:
    """ For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor(15)
    5
    """
    for i in range(2, n+1):
        if (n % i == 0 and n/i == i):
            return i
    return -1

from typing import List


def remove_duplicates(numbers: List[int]) -> List[int]:
    """ From a list of integers, remove all elements that occur more than once.
    Keep order of elements left the same as in the input.
    >>> remove_duplicates([1, 2, 3, 2, 4])
    [1, 3, 4]
    """
    # 找出重复元素
    seen = set()
    for num in numbers:
        if num not in seen:
            seen.add(num)

    return list(seen)

def flip_case(string: str) -> str:
    """ For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
    >>> flip_case('Hello')
    'hELLO'
    """

    return "".join([c if c.isupper() else c.lower() for c in string])

def unique(l: list):
    """Return sorted unique elements in a list
    >>> unique([5, 3, 5, 2, 3, 3, 9, 0, 123])
    [0, 2, 3, 5, 9, 123]
    """
    return sorted(set(l))

def fizz_buzz(n: int):
    """Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
    >>> fizz_buzz(50)
    0
    >>> fizz_buzz(78)
    2
    >>> fizz_buzz(79)
    3
    """
    if n % 11 == 0 and n % 13 == 0:
        return 0
    elif n % 11 == 0:
        return 1
    elif n % 13 == 0:
        return 1
    else:
        return n // 11 + (n // 13 - 1)
