def string_xor(a: str, b: str) -> str:
    """ Input are two strings a and b consisting only of 1s and 0s.
    Perform binary XOR on these inputs and return result also as a string.
    >>> string_xor('010', '110')
    '100'
    """
    if len(b) != len(a):
        raise ValueError("The lengths of the strings to be XORed must be equal")
    x = 0
    for i in range(len(a)):
        x ^= (ord(a[i]) ^ ord(b[i]))
    return "".join([chr(x & 255)] * len(a))

def greatest_common_divisor(a: int, b: int) -> int:
    """ Return a greatest common divisor of two integers a and b
    >>> greatest_common_divisor(3, 5)
    1
    >>> greatest_common_divisor(25, 15)
    5
    """
    if not isinstance(a, int):
        return None
    elif not isinstance(b, int):
        return None

    # If either number is zero then return the other number.
    if (a == 0 or b == 0):
        return b if a == 0 else a

    # Check if both numbers are odd numbers
    if (a % 2 != 0) and (b % 2 != 0):
        return 2 * greatest_common_divisor(a // 2, b // 2)

    # If one number is even, check for divisibility by the other number
    elif (a % 2 == 0) and (b % 2 != 0):
        return greatest_common_divisor(a // 2, b)
    elif (a % 2 != 0) and (b % 2 == 0):
        return greatest_common_divisor(a, b // 2)

    # If neither number is even, check for divisibility by the largest number.
    elif (a % 2 != 0) and (b % 2 != 0):
        return greatest_common_divisor(max(a, b), min(a, b))

    # If both numbers are even, check for divisibility by the smallest number.
    elif (a % 2 == 0) and (b % 2 == 0):
        return greatest_common_divisor(min(a, b), max(a, b))

    # Return the GCD of the two numbers.
    return greatest_common_divisor(a, b)

def largest_divisor(n: int) -> int:
    """ For a given number n, find the largest number that divides n evenly, smaller than n
    >>> largest_divisor(15)
    5
    """
    for i in range(2, int(math.sqrt(n))+1):
        if n % i == 0 and i < n:
            return i
    return -1

