def sieve_of_eratosthenes(n):
    primes = []
    is_prime = [True] * (n+1)
    p = 2
    while (p * p <= n):
        if is_prime[p] == True:
            for i in range(p * p, n+1, p):
                is_prime[i] = False
        p += 1
    for p in range(2, n+1):
        if is_prime[p]:
            primes.append(p)
    return primes

