using namespace std; 

int getSingle(int arr[], int n) 
{ 
	// initialize result 
	int result = 0; 

	// Iterate through every bit 
	for (int i = 0; i < 32; i++) 
	{ 
		// Find sum of set bits at i'th position in all 
		// array elements 
		int x = 0; 
		for (int j = 0; j < n; j++) 
			x += (arr[j] & (1 << i)); 

		// The bits with sum not multiple of 3, are the 
		// bits of element with single occurrence. 
		if (x % 3) 
			result |= (1 << i); 
	} 

	return result; 
} 

