def calculate_paths(matrix):
    rows = len(matrix)
    cols = len(matrix[0])

    dp = [[0 for _ in range(cols)] for _ in range(rows)] 

    dp[0][0] = 1

    for i in range(rows):
        for j in range(cols): 
            if i-1 >= 0: 
                dp[i][j] = dp[i][j] + dp[i-1][j]
            if j-1 >= 0:
                dp[i][j] = dp[i][j] + dp [i][j-1]

    return dp[rows-1][cols-1]

