const Promise = (() => {
  let resolveCallbacks = [];
  let rejectCallbacks = [];

  const promise = (executor) => {
   executor((data) => {
     resolveCallbacks.forEach(callback => {
       callback(data);
     });
   }, (error) => {
     rejectCallbacks.forEach(callback => {
       callback(error);
     });
   });

   return {
     then: (resolve, reject) => {
       resolveCallbacks.push(resolve);
       rejectCallbacks.push(reject);
     }
   };
  };

  return promise;
})();

