int kthSmallestWComp(int arr[], int n, int k) 
{ 
    // Initialize values of left and right 
    int left = -2147483648, right = 2147483647, ans = -1; 
    while (left <= right) 
    { 
        int mid = (left + right) / 2; 
        int countLtEqMid = 0, countGtMid = 0; 

        // Loop to count total less than equal mid 
        for (int i = 0; i < n; i++) 
            if (arr[i] <= mid) 
                countLtEqMid++; 
        // Loop to count total greater than mid 
        for (int i = 0; i < n; i++) 
            if (arr[i] > mid) 
                countGtMid++; 

        if (countLtEqMid < k && k <= (countLtEqMid + countGtMid)) 
        { 
            ans = mid; 
            left = mid + 1; 
        } 
        else if (countLtEqMid >= k) 
            right = mid - 1; 

        else
            left = mid + 1; 
    } 
    return ans; 
}

