def max_difference(numbers):
    if len(numbers) < 2:
        return None
    min_num = numbers[0]
    max_diff = numbers[1] - numbers[0]
    for num in numbers[1:]:
        diff = num - min_num
        if diff > max_diff:
            max_diff = diff
        if num < min_num:
            min_num = num
    return max_diff

