// Function to find GCD 
function findGCD(a, b) { 
    // If both a and b are 0 
    if (a == 0 && b == 0) 
        return 0; 

    // If a is 0, GCD is b 
    if (a == 0) 
        return b; 

    // If b is 0, GCD is a 
    if (b == 0) 
        return a; 

    // Find GCD recursively 
    return findGCD(b, a % b); 
}

