           for (int j = 0; j < n-1-i; j++) {
               if (arr[j] > arr[j+1]) {
                   // Swap arr[j] and arr[j+1]
                   int temp = arr[j];
                   arr[j] = arr[j+1];
                   arr[j+1] = temp;
               }
           }
       }
   }
   public static void main(String[] args) {
       int[] arr = {64, 34, 25, 12, 22, 11, 90};
       bubbleSort(arr);
       System.out.println("Sorted array: ");
       for (int i : arr) {
           System.out.print(i + " ");
       }
   }
}
