def is_prime(num):
    if num < 2:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def sum_of_primes(upper_limit):
    prime_sum = 0
    for i in range(2, upper_limit + 1):
        if is_prime(i):
            prime_sum += i
    return prime_sum

